import React, { useContext } from "react";
import { Link } from "react-router-dom";
import LogoPd from "../images/logo-pd-integrado.png";
import LogoUnicamp from "../images/logo-unicamp.png";
import urls from "../utils/urls";
import {
    AppContext,
} from "../context/AppContext";

const Header = () => {
    const { state } = useContext(AppContext);
    const isAdmin = state.user.authenticated && state.user.username !== null
    const menuItems = [
        {
            name: "Métricas",
            url: urls.metrics,
            disabled: !isAdmin,
        },
        {
            name: "Formulários",
            url: urls.forms,
            disabled: !isAdmin,
        },
        {
            name: "GreenMetric",
            url: urls.greenmetric,
        },
        {
            name: "Entrar",
            url: urls.login,
            disabled: state.user.authenticated,
        },
        {
            name: "Sair",
            url: urls.logout,
            disabled: !state.user.authenticated,
        },
    ];

    return (
        <div className="app-menu">
            <div>
                <img src={LogoPd} />
            </div>
            <div>
                {menuItems.filter((el) => !el.disabled).map((el) => (
                    <a href={el.url}>
                        {el.name}
                    </a>
                ))}
                <img src={LogoUnicamp} />
            </div>
        </div>
    );
};

export default Header;
