import React, { useMemo, useState, useContext } from "react";
import { Icon } from "semantic-ui-react";
import { EditFormsContext, dispatchAddDir } from "../context/EditFormsContext";

const DirectoryItem = ({ item, path = "" }) => {
  const [open, setOpen] = useState(false);
  const { state, dispatch } = useContext(EditFormsContext);
  const id = useMemo(() => (path === "" ? item.name : `${path}/${item.name}`));

  const onClick = (e, id) => {
    e.stopPropagation();
    dispatch(dispatchAddDir(id));
  };

  if (item.hasOwnProperty("children"))
    return (
      <div className="directory" id={id} onClick={(e) => onClick(e, id)}>
        <div onClick={() => setOpen(!open)} className="clickable">
          <Icon name="folder" />
          <p>{item.name}</p>
        </div>
        {open &&
          item.children.map((el) => (
            <DirectoryItem item={el} key={el.name} path={id} />
          ))}
      </div>
    );

  return (
    <div className="metric clickable" id={id} onClick={(e) => onClick(e, id)}>
      <Icon name="file" />
      <p>{item.name}</p>
    </div>
  );
};

export default DirectoryItem;
